import React from 'react';
import './Login.css';

const modal = document.getElementById('id01');

window.onclick = function (event) {
    if (event.target === modal) {
        modal.style.display = "none";
    }
}
const loginIt = document.getElementById('id01');

const Login = () => (




    < div className="loginPage" >
        <button onClick={{ loginIt }} style={{ width: "auto" }}>Login</button>

        <div id="id01" class="modal">


            <form className="modal-content animate" action="/action_page.php">
                <div className="imgcontainer">
                    <span onclick={{ loginIt }} style={{ display: 'block' }} className="close" title="Close Modal">&times;</span>
                    <img src="img_avatar2.png" alt="Avatar" class="avatar" />
                </div>

                <div className="container">
                    <label for="uname"><b>Username</b></label>
                    <input type="text" placeholder="Enter Username" name="uname" required />

                    <label for="psw"><b>Password</b></label>
                    <input type="password" placeholder="Enter Password" name="psw" required />

                    <button type="submit">Login</button>
                    <label>
                        <input type="checkbox" checked="checked" name="remember" /> Remember me
</label>
                </div>

                <div className="container" style={{
                    backgroundColor: "#f1f1f1"
                }} >
                    <button type="button" onClick={{ loginIt }} class="cancelbtn">Cancel</button>
                    <span className="psw">Forgot <a href="#">password?</a></span>
                </div>
            </form>
        </div>



    </div >
)

export default Login;
